jQuery.fn.dataTableExt.oApi.fnAddTr=function(F,G,H){if(typeof H=="undefined"){H=true}var B=G.getElementsByTagName("td");if(B.length!=F.aoColumns.length){alert("Warning: not adding new TR - columns and TD elements must match");return}var C=[];var D=[];var E;for(E=0;E<B.length;E++){C.push(B[E].innerHTML);if(!F.aoColumns[E].bVisible){D.push(E)}}var A=this.oApi._fnAddData(F,C);G._DT_RowIndex=A;F.aoData[A].nTr=G;F.aiDisplay=F.aiDisplayMaster.slice();for(E=(D.length-1);E>=0;E--){F.aoData[A]._anHidden[E]=B[D[E]];G.removeChild(B[D[E]])}if(H){this.oApi._fnReDraw(F)}};